org.contikios.cooja.plugins.ScriptRunner


    log.log("Initializing simulation script\n");

    // Inicia a simulação
    sim.start();
    log.log("Simulation started\n");

    // Aguarda até que o tempo da simulação atinja 10 segundos
    GENERATE_MSG(10000, "Stopping simulation at 10 seconds");

    // Define evento ao atingir 10 segundos
    while (true) {
      YIELD();
      if (time > 10000) {
        log.log("10 seconds reached. Stopping simulation\n");
        sim.stop();
        break;
      }
    }

    // Registra conclusão
    log.log("Simulation stopped successfully\n");

    // Finaliza script
    log.testOK();
